import { Component,EventEmitter, Output, Input } from '@angular/core';
import { ImageUrlTransformService } from '../../services/imageuritransformservice.service';
import { ListComponent } from '../list/list.component';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {

  @Output() closeCard: EventEmitter<any> = new EventEmitter();
  @Output() showListEvent: EventEmitter<any> = new EventEmitter();
  @Input() user: any;


  loader: boolean = false;
  processingMessage: string = "";

  constructor(private imageUrlTransformService: ImageUrlTransformService) { }

  get transformedImageUrl(): string {
    return this.imageUrlTransformService.transform(this.user?.profile_pic_url_hd);
  }


  close() {
    this.closeCard.emit();
  }

  showList(){
    this.showListEvent.emit();
  }

  find(){
    this.loader = true;

  }

  getFormattedCount(count: number): string {
    if (count >= 1000000) {
      return (count / 1000000).toFixed(1) + 'M';
    } else if (count >= 1000) {
      return (count / 1000).toFixed(1) + 'K';
    } else {
      return count.toString();
    }
  }

  load(){
    this.loader = true;
    this.processingMessage = "Sending request";

    let timeoutValue = 0;

    setTimeout(() => {
        this.processingMessage = "Loading Data...";
    }, timeoutValue += 1500);

    setTimeout(() => {
        this.processingMessage = "Getting response from origin master...";
    }, timeoutValue += 3000);

    setTimeout(() => {
        this.processingMessage = "Please be patient..";
    }, timeoutValue += 5000);

    setTimeout(() => {
        this.close();
        this.showList();
    }, timeoutValue += 4000);

  }




}
<body>
    <div class="container">
        <div class="first-grid element-animation">
            <div  class="card color-card">

                <ul>
                    <li (click)="close()" id="back-arrow"><i class="fas fa-arrow-left i-l w"></i></li>
                    <li><i class="fas fa-ellipsis-v i-r w"></i></li>
                    <li><i class="far fa-heart i-r w"></i></li>
                </ul>




                <h1 id="username" class="title">{{user?.username}}</h1>

                <p id="full-name">{{user?.full_name}}</p>

                <div id="biography" class="desc top">
                    <p>{{user?.biography}}</p>
                </div>

                <div id="process">
                    <button *ngIf="!loader" (click)="load()" id="main-btn" class="btn color top">Find Stalkers</button>
                    <img class="loader" *ngIf="loader" src="../../assets/img/loader.gif"/>
                    <p *ngIf="loader">{{processingMessage}}</p>
                </div>

                <hr />

                <div class="engagement">
                    <div class="content">

                        <div class="second-grid">
                            <button class="icon-color circular">
                                <i class="fa-solid fa-image fa-xl"></i>
                            </button>
                            <h5 class="title">Posts</h5>
                            <p id="number-of-posts" class="followers">{{ getFormattedCount(user?.edge_owner_to_timeline_media.count) }}</p>
                        </div>

                        <div class="second-grid">
                            <button class="icon-color circular">
                                <i class="fa-solid fa-users fa-xl"></i>                      
                            </button>
                            <h5 class="title">Followers</h5>
                            <p id="followers" class="followers">{{ getFormattedCount(user?.edge_followed_by.count) }}</p>
                        </div>

                        <div class="second-grid">
                            <button class="icon-color circular">
                                <i class="fa-solid fa-user-plus fa-xl"></i>                      
                            </button>
                            <h5 class="title">Following</h5>
                            <p id="following" class="followers">{{ getFormattedCount(user?.edge_follow.count) }}</p>
                        </div>

                    </div>
                </div>


            </div>

        </div>
    </div>
</body>
